@use "../abstracts" as *;

.trial {
    margin-bottom: 68px;

    @include breakpoint-up(large) {
        margin-bottom: 0;
        width: 100%;
        max-width: 540px;
    }
    &__header {
        color: $white;
        line-height: 26px;
        font-size: 15px;
        font-weight: $regular;
        letter-spacing: 0.27px;
        padding: 18px 66px;
        background-color: $blue;
        border-radius: 10px;
        margin-bottom: 24px;
        box-shadow: 0px 8px 0px 0px hsla(0, 0%, 0%, 0.147);

        @include breakpoint-up(large) {
            padding: 17px 0;
        }

        span {
            font-weight: $bold;
        }
    }

    &__form {
        background-color: $white;
        padding: 24px;
        border-radius: 10px;
        box-shadow: 0px 8px 0px 0px hsla(0, 0%, 0%, 0.147);

        @include breakpoint-up(large) {
            padding: 40px;
        }
    }

    &__input-container {
        position: relative;
        margin-top: 16px;

        @include breakpoint-up(large) {
            margin-top: 20px;
        }

        &-firstName {
            margin-top: 0;
        }
    }

    &__input {
        width: 100%;
        font-size: $font-xs;
        font-weight: $semi-bold;
        line-height: 26px;
        letter-spacing: 0.25px;
        padding: 14px 0px 14px 19.41px;
        border: 1px solid $light-gray;
        border-radius: 5px;

        &::placeholder {
            color: $dark-blue;
            opacity: 75%;
        }

        &:focus,
        &:focus-visible {
            border: 1px solid $blue;
        }

        &-err {
            border: 2px solid $red;
            padding: 13px 0px 13px 19.41px;
        }
    }

    &__btn {
        color: $white;
        font-size: $font-sm;
        font-weight: $semi-bold;
        width: 100%;
        line-height: 26px;
        text-transform: uppercase;
        background-color: $green;
        border: none;
        padding: 15px 0px;
        border-radius: 5px;
        box-shadow: inset 0px -4px 0px 0px hsla(0, 0%, 0%, 9.09%);
        margin-bottom: 8px;
        margin-top: 16px;
        transition: background-color 150ms ease-in-out;

        @include breakpoint-up(large) {
            margin-top: 20px;
        }

        &:hover {
            cursor: pointer;
            background-color: $light-green;
        }
    }

    &__terms {
        color: $grayish-blue;
        font-size: $font-xxs;
        line-height: 21px;
        padding: 0 15px;

        @include breakpoint-up(large) {
            line-height: 26px;
        }
    }
}

//temp hide for error views
.trial__err {
    display: none;
    color: $red;
    font-size: $font-xxs;
    width: 100%;
    text-align: right;
    line-height: 17px;
    margin-top: 6px;
    margin-bottom: -1px;
    font-style: italic;

    &-icon {
        display: none;
        position: absolute;
        top: 16px;
        right: 27px;
    }
}

.show {
    display: block;
}
